00799         public Boolean DetectBlink()
00800         {
00801             try
00802             {
00803                 Boolean ap = false;
00804                 double[] oldval = new double[Records.Count];
00805                 double[] chn1val = new double[Records.Count];
00806               
00807                 if (Records.Count > 0)
00808                 {
00809                     for (int i = 0; i < this.Records.Count; i++)
00810                     {
00811                         oldval[i] = this.Records[i].Alpha1_avgch;
00812                     }
00813                     for (int i = 0; i < this.Records.Count; i++)
00814                     {
00815                         chn1val[i] = this.Records[i].Alpha1_ch1;
00816                     }
00817                    
00818                     if (this.DetectBlinkusingAlpha(oldval, chn1val))
00819                     {
00820                          blinkcnt++;
00821                         
00822 
00823                     }
00824                    if (blinkcnt > 2)//(blinkcnt > 3)
00825                         {
00826 
00827                         Blinked = true;
00828                         Records[Records.Count-1].Blinked = true;
00829                         OnBlinked?.Invoke();
00830                         ap = true;
00831                         blinkcnt = 0;
00832 
00833                     }
00834 
00835 
00836 
00837 
00838 
00839                 }
00840                 return ap;
00841             }
00842             catch (Exception ex)
00843             {
00844 
00845                 CommonTools.ErrorReporting(ex);
00846                 return false;
00847             }
00848         }
00855         public Boolean DetectBlinkusingAlpha(double[] oldvals, double [] chanel1vals)
00856         {
00857             try
00858             {
00859                 Boolean ap = false;
00860             
00861                 for (int i = 0; i < oldvals.Length; i++)
00862                 {
00863                     if (((Calculations.Subtruck(Calculations.AverageAbs(oldvals), oldvals[i]) > VOLTENERG) 
00864                         && (oldvals[i] > VOLTENERG))&&
00865                         ((Calculations.Subtruck(Calculations.AverageAbs(chanel1vals), chanel1vals[i]) > VOLTENERG)
00866                         && (chanel1vals[i] > VOLTENERG))) 
00867                     {
00868                         ap = true;
00869                     }
00870                 }
00871 
00872 
00873 
00874                 return ap;
00875             }
00876             catch (Exception ex)
00877             {
00878 
00879                 CommonTools.ErrorReporting(ex);
00880                 return false;
00881             }
